import React,{useState} from 'react'

export default function Forms(props) {
    const [text, setText] = useState("Enter Something");
    const clickUp = ()=>{
      let newText=text.toUpperCase();
      setText(newText)
    }
    const clickdw = ()=>{
      let newText=text.toLowerCase();
      setText(newText)
    }
    const cleart = ()=>{
      let newText="";
      setText(newText)
    }

    const onChangev = (event)=>{
        setText(event.target.value)}

    return (
       
          <>
          <div className="form-check form-switch">
           <input className="form-check-input" type="checkbox" role="switch" id="flexSwitchCheckDefault"></input>
          <label className="form-check-label" for="flexSwitchCheckDefault">Default switch checkbox input</label>
           </div>
          <div class="form-check" mb4>
          <h1>{props.title}</h1>
        
       <textarea className="area" id="mybox" rows='10'  value={text} onChange={onChangev}></textarea>
      <button className="btn btn-primary" onClick={clickUp}>Convert into uppper case</button>
      <button className="btn btn-primary mx-2" onClick={clickdw}>Convert into lower case</button>
      <button className="btn btn-primary " onClick={cleart}>Clear</button>
       </div>
       <div className="container  mx-3 my-3">
         <p> {text.split(" ").length} words and {text.length} character</p>
         <h4>Previrew</h4>
         <p> {text}</p>     
       </div>
    
       </>
  )
}
