import { useState } from 'react';
import './App.css';
import Forms from './components/Forms';
import Navbar from './components/Navbar';

function App() {
  const [mode,setMode] = useState('light');
  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor= '#212b4b';
    }
  else{
    setMode('light');
    document.body.style.backgroundColor= '#a4a4a4';
  }
  }
  return (
    <>
   <Navbar  title="TEXT" mode={mode} toggleMode={toggleMode}/>
   <div className="container">
    <Forms  title="Enter the data for Analysis" mode={mode}/>
   </div>
    </>
  );
}

export default App;
