import React,{useState} from 'react'

export default function Forms(props) {
    const clickUp = ()=>{
      let newText=text.toUpperCase();
      setText(newText);
    }
    const onChangev = (event)=>{
        setText(event.arget.value)
    const [text, setText] = useState("Enter Something");
    return (
       
          <>
          <div class="form-check" mb4>
          <h1>{props.title}</h1>
        
       <textarea className="area" id="mybox" rows='10'  value={text} onChange={onChangev}></textarea>
      <button className="btn btn-primary" onClick={clickUp}>Convert into uppper case</button>
       </div>
       </>
  )
}
}
