import { useState } from 'react';
import './App.css';
import About from './components/About';
import Alert from './components/Alert';

import Forms from './components/Forms';
import Navbar from './components/Navbar';


function App() {
  const [mode,setMode] = useState('light');
  const [alert, setAlert] = useState(null);
const showAlert =(message,type)=>{
 setAlert(
   {
    message:message,
    type:type
   }

 )
 setTimeout(() => {
    setAlert(null);
}, 2000);
}

  const toggleMode = ()=>{
    if(mode === 'light'){
      setMode('dark');
      document.body.style.backgroundColor= '#212b4b';
      showAlert("DarkMode Enabled","Success");
    }
  else{
    setMode('light');
    document.body.style.backgroundColor= '#e1dfdf';
    showAlert("LightkMode Enabled","Success");
  }
  }
  return (
    <>
   <Navbar  title="TryText" mode={mode} toggleMode={toggleMode}/>
    <Alert  alert={alert}/>
   <div className="container">
   <Router>
   <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/">
          <Forms  title="Enter the Text below" mode={mode} showAlert={showAlert} />
          </Route>
        </Switch>
    </Router>

   </div>
    </>
  );
}

export default App;
